const createLevel = (level: number, words: [string, string, string, string, Category][]): Word[] => {
  return words.map((w, i) => ({
    id: `l${level}-${i}`,
    en: w[0],
    fr: w[1],
    tr: w[2],
    la: w[3],
    category: w[4],
    rarity: level
  }));
};

